// This is the carousel constructor to show images
import React, { Component } from 'react';
import ImgInfo from './utilities/image.json';




export class Carousel extends Component {

  state = { 

  imageGallery: this.props.location.pathname.split('/').pop(),// => captures the gallery name from the url
  imagePathLeft: '',
  imagePathCenter: '',
  imagePathRight: '',

  carouselImageLeft: ImgInfo[this.props.location.pathname.split('/').pop()][4]["image_fileName"],
  carouselImageCenter: ImgInfo[this.props.location.pathname.split('/').pop()][0]["image_fileName"],
  carouselImageRight: ImgInfo[this.props.location.pathname.split('/').pop()][1]["image_fileName"],






  // these are the click counters that are most current - look in right clich btn
  clickCounter: 0, // => this num should dec on left click and incr on right click
  imageGalleryLength: ImgInfo[this.props.location.pathname.split('/').pop()].length, // finds the json array lenth


image: require("./utilities/images/" +  this.props.location.pathname.split('/').pop() + "/" + ImgInfo[this.props.location.pathname.split('/').pop()][0]["image_fileName"]),









  targetPositionLeft: 4,
  targetPositionCenter: 0,
  targetPositionRight: 1,
};

handleClickLeft = (e) => {
e.preventDefault();

const imageGalleryLength = ImgInfo[this.state.imageGallery].length;
const gallery = this.state.imageGallery;
const pathLeft = ImgInfo[this.state.imageGallery][this.state.targetPositionLeft]["image_fileName"]; 
const pathCenter = ImgInfo[this.state.imageGallery][this.state.targetPositionCenter]["image_fileName"];
const pathRight = ImgInfo[this.state.imageGallery][this.state.targetPositionRight]["image_fileName"];  


// => only works with a static number, when I use state it fails
const top = this.state.clickCounter;
const bottom = 4;
  if (this.state.clickCounter > 0) {
   console.log("top");
  this.setState({
    clickCounter: this.state.clickCounter - 1,
    image: require("./utilities/images/" +  this.state.imageGallery + "/" + ImgInfo[this.state.imageGallery][{top}]["image_fileName"]),
  })

  } else {
    console.log("bottom");
      this.setState({
        clickCounter: this.state.imageGalleryLength, // => increasing counter
        image: require("./utilities/images/" +  this.state.imageGallery + "/" + ImgInfo[this.state.imageGallery][bottom]["image_fileName"]),
    });

  }



}






handleClickRight = (e) => {
e.preventDefault();

const imageGalleryLength = ImgInfo[this.state.imageGallery].length;
const gallery = this.state.imageGallery;

const pathLeft = ImgInfo[this.state.imageGallery][this.state.targetPositionLeft]["image_fileName"]; 
const pathCenter = ImgInfo[this.state.imageGallery][this.state.targetPositionCenter]["image_fileName"];
const pathRight = ImgInfo[this.state.imageGallery][this.state.targetPositionRight]["image_fileName"];  




if (this.state.clickCounter < this.state.imageGalleryLength) {
  this.setState({
    clickCounter: this.state.clickCounter + 1,
  })
  } else {
      this.setState({
        clickCounter: 0, // => increasing counter
    });
  }
}

















