  render() {


// console.log("============"); 
// console.log(this.state.targetPositionLeft + " - left");
// console.log(this.state.targetPositionCenter + " - center");
// console.log(this.state.targetPositionRight + "  - right");

console.log("========");
console.log("clickCounter = " + this.state.clickCounter);
console.log("path = " + this.state.image);

    return (

      <div className="carousel-box">
          {/* <img className="left-img" src={this.state.carouselImageLeft} /> */}
          <a className="btn-left" onClick={this.handleClickLeft} ></a>

          <img className="visible-img" src={this.state.image} />

          <a className="btn-right" onClick={this.handleClickRight} ></a>

          {/* <img className="right-img" src={this.state.carouselImageRight} /> */}

      </div>
    )
  }
}

export default Carousel
