// This is the carousel constructor to show images
import React, { Component } from 'react';
import ImgInfo from './utilities/image.json';
import ImageSample from './utilities/images/homes/homesOne.jpg';
import ImageSampleBuild from './utilities/images/landing/LandingFive.jpg';

export class Carousel extends Component {

  state = { 

  imageGalleryName: this.props.location.pathname.split("/").pop(),// => captures the gallery name from the url

  image: ImageSample,
  // imagePathLeft: '',
  // imagePathCenter: '',
  // imagePathRight: '',

  // carouselImageLeft: ImgInfo[this.props.gallery][4]["image_fileName"],
  // carouselImageCenter: ImgInfo[this.props.gallery][0]["image_fileName"],
  // carouselImageRight: ImgInfo[this.props.gallery][1]["image_fileName"],






  // these are the click counters that are most current - look in right clich btn
//   clickCounter: 0, // => this num should dec on left click and incr on right click
//   imageGalleryLength: ImgInfo[this.props.gallery].length, // finds the json array lenth


// image: require("./utilities/images/" +  this.props.gallery + "/" + ImgInfo[this.props.location.pathname.split('/').pop()][0]["image_fileName"]),









  targetPositionLeft: 4,
  targetPositionCenter: 0,
  targetPositionRight: 1,
};

handleClickLeft = (e) => {
e.preventDefault();

// => only works with a static number, when I use state it throws an error
const top = 1;
const bottom = 4;
  if (this.state.clickCounter > 0) {
   console.log("top");
  this.setState({
    clickCounter: this.state.clickCounter - 1,
  })

  } else {
    console.log("bottom");
      this.setState({
        clickCounter: this.state.imageGalleryLength, // => increasing counter
    });

  }



}






handleClickRight = (e) => {
e.preventDefault();

if (this.state.clickCounter < this.state.imageGalleryLength) {
  this.setState({
    clickCounter: this.state.clickCounter + 1,
  })
  } else {
      this.setState({
        clickCounter: 0, // => increasing counter
    });
  }
}

componentDidMount() {
  if (this.state.imageGalleryName === "homes" ) {
    this.setState({
      image: ImageSample,
    })
  } else if (this.state.ImageSample === "buildings" ) {
    this.setState({
      image: ImageSampleBuild,
    })
  } else {
    this.setState({
      image: "the else",
    })
  }
}














  render() {



    console.log(this.state.imageGalleryName);
    return (

      <div className="carousel-box">
          {/* <img className="left-img" src={this.state.carouselImageLeft} /> */}
          <a className="btn-left" onClick={this.handleClickLeft} ></a>

          <img className="visible-img" 
          src={this.state.image}
          alt="placeholder"
          />

          <a className="btn-right" onClick={this.handleClickRight} ></a>

          {/* <img className="right-img" src={this.state.carouselImageRight} /> */}

      </div>
    )
  }
}

export default Carousel
