import React, { Component } from 'react';
import ImgInfo from './components/utilities/image.json';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Home from './components/landing';
import Houses from './components/carousel';
import Nav from './components/nav.js';
import About from './components/about';
import Footer from './components/footer';
import InfoBtn from './components/infoButton';


class App extends Component {
  state = {
    imageIndexNumber: Math.floor(Math.random() * (ImgInfo.landing.length)), // manages the randome number for image path
    imagePath: 'sdkjfbaskd',
    // imageID: this.state.imageIndexNumber,
  };
  render() {
    return (
      <div className="App">

        <Router>
          <Nav />
          <InfoBtn  />
          <Footer />

                <Switch id={this.state.imagePath}>
                  <Route exact path="/" component={Home} />
                  <Route exact path="/about" component={About} />
                  <Route exact path="/houses" component={Houses} />
                </Switch>
            </Router>
      </div>
    );
  }
}

export default App;
