import React, { Component } from 'react';
// import NavProfile from './profile/navProfile';
import About from './profile/about';
import Clients from './profile/clientList';
import Services from './profile/services';




export class Profile extends Component {

  state = {
    isClickedAbout: false,
    isClickedClient: true,
    isClickedServices: true,
    colorAbout: "#f5f5f5",
    colorClients: '#91f1f9',
    colorServices: '#91f1f9',

  }

  handleProfileLinkAbout = (e) => {
    e.preventDefault();
    this.setState({
      isClickedAbout: false,
      isClickedClient: true,
      isClickedServices: true,
    });
    console.log(this.state.isClickedAbout);
    console.log(this.state.isClickedClient);
    console.log(this.state.isClickedServices);
    console.log('About link');
    console.log('-----------------------');
}

handleProfileLinkClients = (e) => {
  e.preventDefault();
  this.setState({
    isClickedAbout: true,
    isClickedClient: false,
    isClickedServices: true,
  });
  console.log(this.state.isClickedAbout);
  console.log(this.state.isClickedClient);
  console.log(this.state.isClickedServices);
  console.log('Clients link');
  console.log('-----------------------');
}

  render() {
    return (
      <div className="profile-box">
          <h1 className="about-title">
            <a 
              id="link"
              href="profile" 
              onClick={this.handleProfileLinkAbout}
              style={{
                color: this.state.colorAbout,
              }}>ABOUT</a>T</h1>
          <h1 className="client-title" >
            <a 
              id="link" 
              href="profile" 
              onClick={this.handleProfileLinkClients} 
              style={{
                color: this.state.colorClients,
              }}>CLIENTS</a></h1>
          <h1 className="services-title">
            <a 
              id="link" 
              onClick={this.handleProfileLink} 
              href="profile" style={{
                color: this.state.colorServices,
              }}>SERVICES</a></h1>
          <About clickStatus={this.state.isClickedAbout} />
          <Clients clickStatus={this.state.isClickedClient} />
          <Services clickStatus={this.state.isClickedServices} />
      </div>
    )
  }
}

export default Profile;
