import React, { Component } from 'react';
import { Link } from 'react-router-dom'



export class follow extends Component {

  state = {
    clickStatus: false,
    left: "-100px",
    transition: "1s",
}
followHandler = (e) => {
  e.preventDefault();
  if (!this.state.clickStatus) {
  this.setState({
    left: "40px",
    clickStatus: true,
  })} else {
    this.setState({
      left: "-100px",
      clickStatus: false,
    })
  }
}
  render() {
    return (
      <div className="follow-parent-box">
        <div className="follow-link-container">
            <button className="follow-link" id="instagram" href="#" 
              style={{
                left: this.state.left,
              }}>instagram</button>
            <button className="follow-link" id="linkeIn" href="#">linkedin</button>
            <button className="follow-link" id="twitter" href="#">twitter</button>
            <button className="follow-link" id="contact" href="#">contact</button>
          </div>
          <button className="follow-BTN" onClick={this.followHandler}>FOLLOW</button>
      </div>
    )
  }
}

export default follow
