   console.log("top");
  this.setState({
    clickCounter: this.state.clickCounter - 1,

  })

  } else {
    console.log("bottom");
      this.setState({
        clickCounter: this.state.imageGalleryLength, // => increasing counter
    });

  }



}






handleClickRight = (e) => {
e.preventDefault();

if (this.state.clickCounter < this.state.imageGalleryLength) {
  this.setState({
    clickCounter: this.state.clickCounter + 1,
  })
  } else {
      this.setState({
        clickCounter: 0, // => increasing counter
    });
  }
}
















  render() {



    console.log(this.state.imageGalleryName);
    return (

      <div className="carousel-box">
          {/* <img className="left-img" src={this.state.carouselImageLeft} /> */}
