// This is the carousel constructor to show images
import React, { Component } from 'react';
import ImgInfo from './utilities/image.json';



import HousesImageOne from './utilities/images/homes/homesOne.jpg';
import HousesImageTwo from './utilities/images/homes/homesTwo.jpg';
import HousesImageThree from './utilities/images/homes/homesThree.jpg';
import HousesImageFour from './utilities/images/homes/homesFour.jpg';
import HousesImageFive from './utilities/images/homes/homesFive.jpg';

export class Carousel extends Component {

  state = { 
  // array of images for the houses carousel

  imageGallery: this.props.location.pathname.split('/').pop(),
  imageGalleryLength: ImgInfo[this.state.imageGallery].length - 1,
  carouselImageLeft: ImgInfo[this.props.location.pathname.split('/').pop()][4]["image_fileName"],
  carouselImageCenter: ImgInfo[this.props.location.pathname.split('/').pop()][0]["image_fileName"],
  carouselImageRight: ImgInfo[this.props.location.pathname.split('/').pop()][1]["image_fileName"],
  // imageLeft: ImgInfo.this.props.location.pathname.split('/').pop()[4],
  // imageCenter: ImgInfo.this.props.location.pathname.split('/').pop()[0],
  // image: ImgInfo.this.props.location.pathname.split('/').pop()[1],

  HousesImage: [ HousesImageOne, HousesImageTwo, HousesImageThree, HousesImageFour, HousesImageFive], 


  targetPositionLeft: 4,
  targetPositionCenter: 0,
  targetPositionRight: 1,
};

handleClickLeft = (e) => {
e.preventDefault();

if (this.state.targetPositionLeft === 0 ) {
    this.setState ({
      targetPositionLeft: 4,
      targetPositionCenter: 0,
      targetPositionRight: 1,

      imageGallery: this.props.location.pathname.split('/').pop(),
      carouselImageLeft: ImgInfo[this.state.imageGallery][4]["image_fileName"],
      carouselImageCenter: ImgInfo[this.props.location.pathname.split('/').pop()][0]["image_fileName"],
      carouselImageRight: ImgInfo[this.props.location.pathname.split('/').pop()][1]["image_fileName"]


    });

    console.log("---------------");
    console.log("left " + this.state.carouselImageLeft);
    console.log("center " + this.state.carouselImageCenter);
    console.log("right " +this.state.carouselImageRight);


  } else if (this.state.targetPositionRight === 0) {
    this.setState ({
      targetPositionLeft: 2,
      targetPositionCenter: 3,
      targetPositionRight: 4,


      imageGallery: this.props.location.pathname.split('/').pop(),
      carouselImageLeft: ImgInfo[this.props.location.pathname.split('/').pop()][2]["image_fileName"],
      carouselImageCenter: ImgInfo[this.props.location.pathname.split('/').pop()][3]["image_fileName"],
      carouselImageRight: ImgInfo[this.props.location.pathname.split('/').pop()][4]["image_fileName"]


    });
    console.log("---------------");
    console.log("left " + this.state.carouselImageLeft);
    console.log("center " + this.state.carouselImageCenter);
    console.log("right " +this.state.carouselImageRight);
  } 

  else if (this.state.targetPositionCenter === 0) {
     this.setState({
        targetPositionLeft: 3,
        targetPositionCenter: 4,
        targetPositionRight: 0,

        imageGallery: this.props.location.pathname.split('/').pop(),
        carouselImageLeft: ImgInfo[this.props.location.pathname.split('/').pop()][3]["image_fileName"],
        carouselImageCenter: ImgInfo[this.props.location.pathname.split('/').pop()][4]["image_fileName"],
        carouselImageRight: ImgInfo[this.props.location.pathname.split('/').pop()][0]["image_fileName"]


      });
      console.log("---------------");
      console.log("left " + this.state.carouselImageLeft);
      console.log("center " + this.state.carouselImageCenter);
      console.log("right " +this.state.carouselImageRight);
  }

  else {
    this.setState ({
       targetPositionLeft: this.state.targetPositionLeft - 1,
        targetPositionCenter: this.state.targetPositionCenter - 1,
        targetPositionRight: this.state.targetPositionRight - 1,

        imageGallery: this.props.location.pathname.split('/').pop(),
        carouselImageLeft: ImgInfo[this.props.location.pathname.split('/').pop()][this.state.targetPositionLeft - 1]["image_fileName"],
        carouselImageCenter: ImgInfo[this.props.location.pathname.split('/').pop()][this.state.targetPositionLeft - 1]["image_fileName"],
        carouselImageRight: ImgInfo[this.props.location.pathname.split('/').pop()][this.state.targetPositionLeft - 1]["image_fileName"]


    });
    console.log("---------------");
    console.log("left " + this.state.carouselImageLeft);
    console.log("center " + this.state.carouselImageCenter);
    console.log("right " +this.state.carouselImageRight);
  }




}

handleClickRight = (e) => {
e.preventDefault();
console.log("right");
console.log(this.state.targetPositionRight);

}
handleCarouselImageLoading = () => {
  console.log(this.state.carouselImageRight);
}

  render() {

//set the state for the images here with a function.

// const galleryName = this.state.imageGallery;
// const carouselImage = ImgInfo[galleryName][1];
console.log("---------------");
console.log(ImgInfo[this.state.imageGallery].length - 1);
console.log("---------------");
console.log("Load - left " + this.state.carouselImageLeft);
console.log("Load - center " + this.state.carouselImageCenter);
console.log("Load - right " +this.state.carouselImageRight);
    return (

      <div className="carousel-box">
          <img className="left-img" src={this.state.HousesImage[this.state.targetPositionLeft]} />
          <button className="btn-left" onClick={this.handleClickLeft} ></button>

          <img className="visible-img" src={this.state.HousesImage[this.state.targetPositionCenter]} />

          <button className="btn-right" onClick={this.handleClickRight} ></button>

          <img className="right-img" src={this.state.HousesImage[this.state.targetPositionRight]} />

      </div>
    )
  }
}

export default Carousel
